import { useState } from "react";
import styled from "styled-components";
import { Star, Constellation } from "../../types/types";
import Dropdown from "../../components/Dropdown";
import useAllStars from "../../hooks/useAllStars";
import useAllConstellations from "../../hooks/useAllConstellations";

const StarsPage = () => {
  const [dropdownValue, setDropdownValue] = useState<Constellation>();
  const [addView, setAddView] = useState(false);
  const [selectedStar, setSelectedStar] = useState<Star>();

  const { allStars } = useAllStars();
  const { allConstellations } = useAllConstellations();

  const addConstellationToStarView = (star: Star) => {
    console.log(star.id);
    setSelectedStar(star);
    setAddView(true);
  };

  const addStarConstellation = async () => {
    console.log(selectedStar?.id, dropdownValue?.id);

    const data = {
      starID: selectedStar?.id,
      constellationID: dropdownValue?.id,
    };
    const apiResponse = await fetch(
      "http://localhost:777/addStarConstellation",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      }
    );
    const responseData = await apiResponse.json();
    console.log(responseData);
  };

  return (
    <Container>
      <div>
        {allStars?.map((star) => (
          <div key={star.id}>
            <h3>{star.id}</h3>
            <h3>{star.name}</h3>
            <h3>{star.description}</h3>
            <h3>{star.link}</h3>
            <button onClick={() => addConstellationToStarView(star)}>
              Dodaj konstelacje
            </button>
          </div>
        ))}
      </div>
      {addView && (
        <ConstellationToStarContainer>
          <ConstellationToStarWrapper>
            <p>Dodaj KonstelacjÄ™ do gwiazdy</p>
            <p>Nazwa gwiazdy: {selectedStar?.name}</p>
            <p>ID gwiazdy: {selectedStar?.id}</p>
            <Dropdown
              placeHolder="Select...."
              options={allConstellations}
              handleValue={setDropdownValue}
            />
            <button onClick={() => addStarConstellation()}>Dodaj</button>
          </ConstellationToStarWrapper>
        </ConstellationToStarContainer>
      )}
    </Container>
  );
};

export default StarsPage;

const Container = styled.div`
  display: flex;
`;

const ConstellationToStarContainer = styled.div`
  display: fixed;
  top: 50%;
  left: 50%;
  background-color: wheat;
  width: 300px;
  height: 300px;
`;

const ConstellationToStarWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
