import React, { useState, useEffect } from "react";
import { AllStars, Star } from "./types/types";
import Dropdown from "./components/Dropdown";
import styled from "styled-components";

function App() {
  const [dropdownValue, setDropdownValue] = useState<undefined | string>("");
  const [addView, setAddView] = useState(false);
  const [selectedStar, setSelectedStar] = useState<Star>();
  const [allStarsHook, setAllStarsHook] = useState<AllStars>([
    {
      id: 669,
      name: "test",
      description: "test",
      link: "test",
    },
  ]);

  const getStars = async () => {
    try {
      const apiResponse = await fetch("http://localhost:777/getStars", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = await apiResponse.json();
      setAllStarsHook(json.stars);
    } catch (e) {
      console.log(e);
    }
  };

  const addKonstelationToStar = (star: Star) => {
    console.log(star.id);
    setSelectedStar(star);
    setAddView(true);
  };

  useEffect(() => {
    getStars();
  }, []);

  return (
    <Container>
      <Dropdown
        placeHolder="Select...."
        options={allStarsHook}
        handleValue={setDropdownValue}
      />
      <div>
        {allStarsHook?.map((star) => (
          <div key={star.id}>
            <h3>{star.id}</h3>
            <h3>{star.name}</h3>
            <h3>{star.description}</h3>
            <h3>{star.link}</h3>
            <button onClick={() => addKonstelationToStar(star)}>
              Dodaj konstelacje
            </button>
          </div>
        ))}
      </div>
      {addView && (
        <KonstelationToStarContainer>
          <KonstelationToStarWrapper>
            <p>Dodaj KonstelacjÄ™ do gwiazdy</p>
            <p>Nazwa gwiazdy: {selectedStar?.name}</p>
            <p>ID gwiazdy: {selectedStar?.id}</p>
            <Dropdown
              placeHolder="Select...."
              options={allStarsHook}
              handleValue={setDropdownValue}
            />
          </KonstelationToStarWrapper>
        </KonstelationToStarContainer>
      )}
    </Container>
  );
}

export default App;

const Container = styled.div`
  display: flex;
`;

const KonstelationToStarContainer = styled.div`
  display: fixed;
  top: 50%;
  left: 50%;
  background-color: wheat;
  width: 200px;
  height: 200px;
`;

const KonstelationToStarWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
