import React, { useState, useEffect } from "react";
import {
  AllStars,
  Star,
  AllConstellations,
  Constellation,
} from "./types/types";
import Dropdown from "./components/Dropdown";
import styled from "styled-components";

function App() {
  const [dropdownValue, setDropdownValue] = useState<Constellation>();
  const [addView, setAddView] = useState(false);
  const [selectedStar, setSelectedStar] = useState<Star>();
  const [allStarsHook, setAllStarsHook] = useState<AllStars>();
  const [allConstellationsHook, setAllConstellationsHook] =
    useState<AllConstellations>();

  const getStars = async () => {
    try {
      const apiResponse = await fetch("http://localhost:777/getStars", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = await apiResponse.json();
      setAllStarsHook(json.stars);
    } catch (e) {
      console.log(e);
    }
  };

  const getConstellations = async () => {
    try {
      const apiResponse = await fetch(
        "http://localhost:777/getConstellations",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const json = await apiResponse.json();
      setAllConstellationsHook(json.Constellations);
    } catch (e) {
      console.log(e);
    }
  };

  const addConstellationToStarView = (star: Star) => {
    console.log(star.id);
    setSelectedStar(star);
    setAddView(true);
  };

  const addConstellationToStar = async () => {
    console.log(selectedStar?.id, dropdownValue?.id);

    const data = {
      starID: selectedStar?.id,
      constellationID: dropdownValue?.id,
    };
    const apiResponse = await fetch(
      "http://localhost:777/addStarConstellation",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      }
    );
    console.log(apiResponse);
  };

  useEffect(() => {
    getStars();
    getConstellations();
  }, []);

  return (
    <Container>
      {/* <Dropdown
        placeHolder="Select...."
        options={allStarsHook}
        handleValue={setDropdownValue}
      /> */}
      <div>
        {allStarsHook?.map((star) => (
          <div key={star.id}>
            <h3>{star.id}</h3>
            <h3>{star.name}</h3>
            <h3>{star.description}</h3>
            <h3>{star.link}</h3>
            <button onClick={() => addConstellationToStarView(star)}>
              Dodaj konstelacje
            </button>
          </div>
        ))}
      </div>
      {addView && (
        <ConstellationToStarContainer>
          <ConstellationToStarWrapper>
            <p>Dodaj KonstelacjÄ™ do gwiazdy</p>
            <p>Nazwa gwiazdy: {selectedStar?.name}</p>
            <p>ID gwiazdy: {selectedStar?.id}</p>
            <Dropdown
              placeHolder="Select...."
              options={allConstellationsHook}
              handleValue={setDropdownValue}
            />
            <button onClick={() => addConstellationToStar()}>Dodaj</button>
          </ConstellationToStarWrapper>
        </ConstellationToStarContainer>
      )}
    </Container>
  );
}

export default App;

const Container = styled.div`
  display: flex;
`;

const ConstellationToStarContainer = styled.div`
  display: fixed;
  top: 50%;
  left: 50%;
  background-color: wheat;
  width: 300px;
  height: 300px;
`;

const ConstellationToStarWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
