import React, { useState, useEffect } from "react";
import { AllStars } from "./types/types";
import Dropdown from "./components/Dropdown";
import styled from "styled-components";

function App() {
  const [dropdownValue, setDropdownValue] = useState(0);
  const [allStarsHook, setAllStarsHook] = useState<AllStars>([
    {
      id: 669,
      name: "test",
      description: "test",
      link: "test",
    },
  ]);

  const getStars = async () => {
    try {
      const apiResponse = await fetch("http://localhost:777/getStars", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const json = await apiResponse.json();
      setAllStarsHook(json.stars);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getStars();
  }, []);

  const getValueFromDropdown = (value: number) => {
    setDropdownValue(value);
  };

  return (
    <Container>
      <Dropdown
        placeHolder="Select...."
        options={allStarsHook}
        handleValue={getValueFromDropdown}
      />
      <div>
        {allStarsHook?.map((star) => (
          <div key={star.id}>
            <h3>{star.id}</h3>
            <h3>{star.name}</h3>
            <h3>{star.description}</h3>
            <h3>{star.link}</h3>
          </div>
        ))}
      </div>
    </Container>
  );
}

export default App;

const Container = styled.div`
  display: flex;
`;
